mapper.select:
SELECT CropID, CropName, ScientificName, Category, Description, CreatedAt, UpdatedAt
FROM Crop
WHERE CropID = #{CropID}

mapper.update:
UPDATE Crop
SET CropName = #{CropName},
    ScientificName = #{ScientificName},
    Category = #{Category},
    Description = #{Description}
WHERE CropID = #{CropID}
